import React, { useEffect, useState } from "react";
import { Button, Card, Container, Navbar, Nav } from "react-bootstrap";
const axios = require("axios");
const News = () => {
  const [news, setNews] = useState([]);
  const [page, setPage] = useState("1");
  const updateNews = (pageNumber) => {
    setPage(pageNumber);

  };
  const getArticles = () => {
    axios
      .get(`http://localhost:3001/news/${page}`)
      .then(function (response) {
        setNews(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  useEffect(() => {
    getArticles();
  }, []);
  console.log(news);
  return (
    <Container id="top" className="news-container">
      <div className="news-header-container">
        <h1>ARTICLES</h1>
      </div>
      <Navbar bg="light" variant="light">
        <Container className="navbar-container">
          <Navbar.Brand href="#home">News</Navbar.Brand>
          <Nav className="me-auto">
            <Button 
              onClick={() => {
                updateNews(1);
              }}
            >
              1
            </Button>
            <Button
              onClick={() => {
                updateNews(2);
              }}
            >
              2
            </Button>
            <Button
              onClick={() => {
                updateNews(3);
              }}
            >
              3
            </Button>
            <Button
              onClick={() => {
                updateNews(4);
              }}
            >
              4
            </Button>
            <Button
              onClick={() => {
                updateNews(5);
              }}
            >
              5
            </Button>
            <Button
              onClick={() => {
                updateNews(6);
              }}
            >
              6
            </Button>
          </Nav>
        </Container>
      </Navbar>
      {news.map((article, idx) => (
        <Card key={idx} className="article-card">
          <Card.Img variant="top" src={article.cover_image} />
          <Card.Body>
            <Card.Title style={{ fontSize: "2rem" }}>
              {article.title}
            </Card.Title>
            <Card.Text>{article.description}</Card.Text>
            <Button
              size="lg"
              variant="primary"
              target="_blank"
              href={article.url}
            >
              read more
            </Button>
          </Card.Body>
          <Card.Footer className="text-muted">
            Created at {article.readable_publish_date}
          </Card.Footer>
        </Card>
      ))}

      <div className="d-grid gap-2 mt-4">
        <Button href="#top" className="backBtn" size="lg">
          Back to top
        </Button>
      </div>
    </Container>
  );
};

export default News;
